<?php$senderName		= $_POST['nombre'];$senderEmail	= $_POST['correo'];$senderPhone	= $_POST['telefono'];$senderMessage	= $_POST['mensaje'];$to 			= "javier.water.energy@gmail.com";$headers  		= "MIME-Version: 1.0\r\n"; $headers 		.= "Content-type: text/html; charset=utf-8\r\n";$headers 		.= "From: web@clubelite.com.ar\r\n";$headers 		.= "Reply-To: " . $senderEmail . "\r\n";$toSubject 		= "Mensaje desde la Web Water-Energy";$emailBody 		= "De: $senderName<br>				Email: $senderEmail<br>				Telefono: $senderPhone<br><br>				Mensaje:<br>				". nl2br($senderMessage);  				$message 		= $emailBody;if( $senderEmail == $to ){	echo "Por favor coloca TU dirección de correo!";	die();}  if( mail($to, $toSubject, $message, $headers) ){	header('Location: ../contacto.php?ok=ok');	echo 0;	die();	}else{	echo "Servidor ocupado, por favor intente más tarde. Gracias!";	die();}?>